mapper.select:
	SELECT

	FROM
	sys_user 
	WHERE 1=1

      AND ORG_ID = #{orgId,jdbcType=BIGINT}



     AND USER_CH_NAME like CONCAT(CONCAT('%',#{userChName,jdbcType=CHAR},'%'))



     AND MOBILE_PHONE  like CONCAT(CONCAT('%',#{mobilePhone,jdbcType=CHAR},'%')) 



     AND USER_SEX = #{userSex,jdbcType=SMALLINT},



     AND  PROVINCE_ID = #{provinceId,jdbcType=INTEGER}


     AND PROVINCE_NAME = #{provinceName,jdbcType=CHAR}


     AND CITY_ID = #{cityId,jdbcType=INTEGER}


     AND CITY_NAME = #{cityName,jdbcType=CHAR}


     AND COUNTRY_ID = #{countryId,jdbcType=INTEGER}


     AND CONTRY_NAME = #{contryName,jdbcType=CHAR}

   ORDER BY USER_ID ASC 
   LIMIT
   #{startIndex,jdbcType=BIGINT} ,#{pageSize,jdbcType=BIGINT};

	SELECT
	count(*)
	FROM
	sys_user 
	WHERE 1=1


      AND ORG_ID = #{orgId,jdbcType=BIGINT}



     AND USER_CH_NAME like CONCAT(CONCAT('%',#{userChName,jdbcType=CHAR},'%'))



     AND MOBILE_PHONE  like CONCAT(CONCAT('%',#{mobilePhone,jdbcType=CHAR},'%')) 



     AND USER_SEX = #{userSex,jdbcType=SMALLINT},



     AND  PROVINCE_ID = #{provinceId,jdbcType=INTEGER}


     AND PROVINCE_NAME = #{provinceName,jdbcType=CHAR}


     AND CITY_ID = #{cityId,jdbcType=INTEGER}


     AND CITY_NAME = #{cityName,jdbcType=CHAR}


     AND COUNTRY_ID = #{countryId,jdbcType=INTEGER}


     AND CONTRY_NAME = #{contryName,jdbcType=CHAR}
